// LOOP Entity Schema Validator
// Validates Task entities against schema_constants.yaml

const tasks = $input.all().map(item => item.json.tasks || []).flat();
const results = [];

// Valid values from schema_constants.yaml
const VALID_STATUS = ['todo', 'doing', 'done', 'blocked'];
const VALID_PRIORITY = ['critical', 'high', 'medium', 'low'];
const VALID_TYPES = ['dev', 'strategy', 'research', 'ops', null];
const VALID_TARGET_PROJECTS = ['sosi', 'kkokkkok', 'loop-api', 'loop', null];
const VALID_CONDITIONS = ['cond-a', 'cond-b', 'cond-c', 'cond-d', 'cond-e'];

for (const task of tasks) {
  const issues = [];
  const suggestions = {};
  const reasoning = {};

  // Required field checks
  if (!task.entity_id) {
    issues.push('missing_entity_id');
  }
  if (!task.entity_name) {
    issues.push('missing_entity_name');
  }
  if (!task.project_id) {
    issues.push('missing_project_id');
  }

  // Status validation
  if (task.status && !VALID_STATUS.includes(task.status)) {
    issues.push('invalid_status');
    suggestions.status = 'todo';
    reasoning.status = `Invalid status "${task.status}". Defaulting to "todo".`;
  }

  // Priority validation
  if (task.priority && !VALID_PRIORITY.includes(task.priority)) {
    issues.push('invalid_priority');
    suggestions.priority = 'medium';
    reasoning.priority = `Invalid priority "${task.priority}". Defaulting to "medium".`;
  }

  // Type validation
  if (task.type && !VALID_TYPES.includes(task.type)) {
    issues.push('invalid_type');
    suggestions.type = null;
    reasoning.type = `Invalid type "${task.type}". Setting to null.`;
  }

  // conditions_3y validation (required for Tasks)
  if (!task.conditions_3y || task.conditions_3y.length === 0) {
    issues.push('missing_conditions_3y');
    // This needs LLM to infer - mark for pending review
  }

  // assignee check
  if (!task.assignee) {
    issues.push('missing_assignee');
  }

  // due date check
  if (!task.due && !task.due_date) {
    issues.push('missing_due_date');
  }

  if (issues.length > 0) {
    results.push({
      entity_id: task.entity_id,
      entity_name: task.entity_name,
      entity_type: 'Task',
      issues: issues,
      deterministic_fixes: suggestions,
      needs_llm: issues.includes('missing_conditions_3y'),
      reasoning: reasoning,
      original_task: task
    });
  }
}

return results.map(r => ({ json: r }));

// Parse LLM response and prepare pending review
const input = $input.first().json;
const llmResponse = input.message?.content || input.text || '';

let parsed;
try {
  // Extract JSON from response (handle markdown code blocks)
  const jsonMatch = llmResponse.match(/```json\n?([\s\S]*?)\n?```/) || 
                   llmResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    parsed = JSON.parse(jsonMatch[1] || jsonMatch[0]);
  }
} catch (e) {
  parsed = { suggested_fields: {}, reasoning: { error: 'Failed to parse LLM response' } };
}

return [{
  json: {
    entity_id: $('Validate Schema').first().json.entity_id,
    entity_type: 'Task',
    entity_name: $('Validate Schema').first().json.entity_name,
    suggested_fields: parsed.suggested_fields || {},
    reasoning: parsed.reasoning || {}
  }
}];
