// LOOP Entity Schema Validator v2
// - done/blocked 상태는 due_date 체크 스킵
// - missing_due_date, missing_assignee는 LLM으로 보내기
// - deterministic_fixes가 비어있으면 결과에서 제외

const tasks = $input.all().map(item => item.json.tasks || []).flat();
const results = [];

// Valid values from schema_constants.yaml
const VALID_STATUS = ['todo', 'doing', 'hold', 'done', 'blocked'];
const VALID_PRIORITY = ['critical', 'high', 'medium', 'low'];
const VALID_TYPES = ['dev', 'strategy', 'research', 'ops', null];
const VALID_CONDITIONS = ['cond-a', 'cond-b', 'cond-c', 'cond-d', 'cond-e'];

// LLM이 필요한 이슈 목록
const LLM_REQUIRED_ISSUES = ['missing_conditions_3y', 'missing_due_date', 'missing_assignee'];

for (const task of tasks) {
  const issues = [];
  const suggestions = {};
  const reasoning = {};

  // Required field checks
  if (!task.entity_id) {
    issues.push('missing_entity_id');
  }
  if (!task.entity_name) {
    issues.push('missing_entity_name');
  }
  if (!task.project_id) {
    issues.push('missing_project_id');
  }

  // Status validation
  if (task.status && !VALID_STATUS.includes(task.status)) {
    issues.push('invalid_status');
    suggestions.status = 'todo';
    reasoning.status = `Invalid status "${task.status}". Defaulting to "todo".`;
  }

  // Priority validation
  if (task.priority && !VALID_PRIORITY.includes(task.priority)) {
    issues.push('invalid_priority');
    suggestions.priority = 'medium';
    reasoning.priority = `Invalid priority "${task.priority}". Defaulting to "medium".`;
  }

  // Type validation
  if (task.type && !VALID_TYPES.includes(task.type)) {
    issues.push('invalid_type');
    suggestions.type = null;
    reasoning.type = `Invalid type "${task.type}". Setting to null.`;
  }

  // conditions_3y validation (required for Tasks)
  if (!task.conditions_3y || task.conditions_3y.length === 0) {
    issues.push('missing_conditions_3y');
    reasoning.conditions_3y = 'Task는 3Y Condition 연결 필수. LLM 추론 필요.';
  }

  // assignee check
  if (!task.assignee) {
    issues.push('missing_assignee');
    reasoning.assignee = 'assignee 미지정. 맥락 기반 추론 필요.';
  }

  // due date check - todo/doing 상태만 체크 (done/blocked는 스킵)
  const needsDueDate = ['todo', 'doing'].includes(task.status);
  if (needsDueDate && !task.due && !task.due_date) {
    issues.push('missing_due_date');
    reasoning.due = 'due_date 미지정. 맥락 기반 추론 필요.';
  }

  // 이슈가 있는 경우만 결과에 추가
  if (issues.length > 0) {
    // LLM 필요 여부 판단
    const needsLlm = issues.some(issue => LLM_REQUIRED_ISSUES.includes(issue));

    // deterministic_fixes가 비어있고 LLM도 필요없으면 스킵
    const hasDeterministicFixes = Object.keys(suggestions).length > 0;
    if (!needsLlm && !hasDeterministicFixes) {
      continue; // 의미없는 pending 생성 방지
    }

    results.push({
      entity_id: task.entity_id,
      entity_name: task.entity_name,
      entity_type: 'Task',
      issues: issues,
      deterministic_fixes: suggestions,
      needs_llm: needsLlm,
      has_deterministic_fixes: hasDeterministicFixes,
      reasoning: reasoning,
      original_task: task
    });
  }
}

return results.map(r => ({ json: r }));

// Parse LLM response and prepare pending review
const input = $input.first().json;
const validateData = $('Validate Schema').first().json;
const llmResponse = input.message?.content || input.text || input.content || '';

let parsed = { suggested_fields: {}, reasoning: {} };

try {
  // Extract JSON from response (handle markdown code blocks)
  let jsonStr = llmResponse;
  const jsonMatch = llmResponse.match(/```json\n?([\s\S]*?)\n?```/);
  if (jsonMatch) {
    jsonStr = jsonMatch[1];
  } else {
    const objMatch = llmResponse.match(/\{[\s\S]*\}/);
    if (objMatch) jsonStr = objMatch[0];
  }
  parsed = JSON.parse(jsonStr);
} catch (e) {
  parsed = { 
    suggested_fields: {}, 
    reasoning: { error: `Failed to parse LLM response: ${e.message}` } 
  };
}

return [{
  json: {
    entity_id: validateData.entity_id,
    entity_type: 'Task',
    entity_name: validateData.entity_name,
    suggested_fields: parsed.suggested_fields || {},
    reasoning: parsed.reasoning || {}
  }
}];
