// LOOP Project Impact Filter v1
// - expected_impact 없는 프로젝트 필터링
// - done 상태 + realized_impact 없는 프로젝트 필터링

const projects = $input.all().map(item => item.json.projects || []).flat();
const results = [];

for (const project of projects) {
  // Skip if no entity_id
  if (!project.entity_id) continue;

  // A: expected_impact 없는 프로젝트
  const expectedImpact = project.expected_impact;
  const needsExpected = !expectedImpact || 
                        !expectedImpact.tier || 
                        expectedImpact.tier === null ||
                        expectedImpact.tier === 'none';

  // B: done 상태 + realized_impact 없는 프로젝트
  const realizedImpact = project.realized_impact;
  const isDone = project.status === 'done';
  const needsRealized = isDone && 
                        (!realizedImpact || 
                         realizedImpact.normalized_delta === undefined ||
                         realizedImpact.normalized_delta === null);

  // expected_impact 필요한 경우
  if (needsExpected) {
    results.push({
      entity_id: project.entity_id,
      entity_name: project.entity_name,
      entity_type: 'Project',
      impact_type: 'expected',
      original_project: project
    });
  }

  // realized_impact 필요한 경우
  if (needsRealized) {
    results.push({
      entity_id: project.entity_id,
      entity_name: project.entity_name,
      entity_type: 'Project',
      impact_type: 'realized',
      original_project: project
    });
  }
}

// 결과가 없으면 빈 배열 반환 (워크플로우 종료)
if (results.length === 0) {
  return [];
}

return results.map(r => ({ json: r }));

// Parse LLM response for Expected Impact
const input = $input.first().json;
const filterData = $('Filter Projects').first().json;
const llmResponse = input.message?.content || input.text || input.content || '';

let parsed = { suggested_fields: {}, reasoning: {} };

try {
  // Extract JSON from response (handle markdown code blocks)
  let jsonStr = llmResponse;
  const jsonMatch = llmResponse.match(/```json\n?([\s\S]*?)\n?```/);
  if (jsonMatch) {
    jsonStr = jsonMatch[1];
  } else {
    const objMatch = llmResponse.match(/\{[\s\S]*\}/);
    if (objMatch) jsonStr = objMatch[0];
  }
  parsed = JSON.parse(jsonStr);
} catch (e) {
  parsed = { 
    suggested_fields: {}, 
    reasoning: { error: `Failed to parse LLM response: ${e.message}` } 
  };
}

return [{
  json: {
    entity_id: filterData.entity_id,
    entity_type: 'Project',
    entity_name: filterData.entity_name,
    suggested_fields: parsed.suggested_fields || {},
    reasoning: parsed.reasoning || {}
  }
}];

// Parse LLM response for Realized Impact
const input = $input.first().json;
const filterData = $('Filter Projects').first().json;
const llmResponse = input.message?.content || input.text || input.content || '';

let parsed = { suggested_fields: {}, reasoning: {} };

try {
  // Extract JSON from response (handle markdown code blocks)
  let jsonStr = llmResponse;
  const jsonMatch = llmResponse.match(/```json\n?([\s\S]*?)\n?```/);
  if (jsonMatch) {
    jsonStr = jsonMatch[1];
  } else {
    const objMatch = llmResponse.match(/\{[\s\S]*\}/);
    if (objMatch) jsonStr = objMatch[0];
  }
  parsed = JSON.parse(jsonStr);
} catch (e) {
  parsed = { 
    suggested_fields: {}, 
    reasoning: { error: `Failed to parse LLM response: ${e.message}` } 
  };
}

return [{
  json: {
    entity_id: filterData.entity_id,
    entity_type: 'Project',
    entity_name: filterData.entity_name,
    suggested_fields: parsed.suggested_fields || {},
    reasoning: parsed.reasoning || {}
  }
}];
