// Failure notification message with error handling
const item = $input.first();

// Guard: Check if input exists
if (!item || !item.json) {
  return [{ json: { 
    message: '‚ö†Ô∏è YouTube Weekly ÎùºÏö¥Îìú ÏÉùÏÑ± Ïã§Ìå®\n\nError: ÏùëÎãµ Îç∞Ïù¥ÌÑ∞ ÏóÜÏùå',
    success: false,
    error: { type: 'no_data', detail: 'No response data' }
  }}];
}

const data = item.json;

// Detect error type: n8n HTTP error vs API error
let errorMessage = '';
let errorDetail = {};

// Case 1: n8n HTTP error (has error object)
if (data.error && typeof data.error === 'object') {
  errorMessage = data.error.message || data.error.description || data.reason || 'Unknown HTTP error';
  errorDetail = {
    type: 'http_error',
    message: errorMessage
  };
}
// Case 2: n8n HTTP error (string message)
else if (data.error && typeof data.error === 'string') {
  errorMessage = data.error;
  errorDetail = {
    type: 'http_error',
    message: errorMessage
  };
}
// Case 3: API returned error (detail field from FastAPI HTTPException)
else if (data.detail) {
  errorMessage = typeof data.detail === 'string' ? data.detail : JSON.stringify(data.detail);
  errorDetail = {
    type: 'api_error',
    message: errorMessage,
    detail: data.detail
  };
}
// Case 4: Other API error
else {
  errorMessage = typeof data.message === 'string' ? data.message : JSON.stringify(data);
  errorDetail = {
    type: 'api_error',
    message: errorMessage,
    body: data
  };
}

// Truncate long error messages
if (errorMessage.length > 500) {
  errorMessage = errorMessage.substring(0, 500) + '...';
}

const message = `‚ö†Ô∏è YouTube Weekly ÎùºÏö¥Îìú ÏÉùÏÑ± Ïã§Ìå®\n\nError: ${errorMessage}`;

return [{ json: { message, success: false, error: errorDetail } }];
